"use strict";(self.webpackChunkmy_website=self.webpackChunkmy_website||[]).push([[547],{4950:o=>{o.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"category","label":"Introduction","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Introduction","href":"/Apps4xDocs-Docusaurus/docs/Introduction/","docId":"Introduction/Introduction","unlisted":false}],"href":"/Apps4xDocs-Docusaurus/docs/category/introduction"},{"type":"category","label":"Organization","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Organization","href":"/Apps4xDocs-Docusaurus/docs/Organization/","docId":"Organization/Organization","unlisted":false},{"type":"link","label":"Environment","href":"/Apps4xDocs-Docusaurus/docs/Organization/Environment","docId":"Organization/Environment","unlisted":false},{"type":"link","label":"Company","href":"/Apps4xDocs-Docusaurus/docs/Organization/Company","docId":"Organization/Company","unlisted":false}],"href":"/Apps4xDocs-Docusaurus/docs/category/organization"},{"type":"category","label":"Apps","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Apps","href":"/Apps4xDocs-Docusaurus/docs/Apps/","docId":"Apps/Apps","unlisted":false},{"type":"link","label":"Collection","href":"/Apps4xDocs-Docusaurus/docs/Apps/Collection","docId":"Apps/Collection","unlisted":false},{"type":"link","label":"Entity","href":"/Apps4xDocs-Docusaurus/docs/Apps/Entity","docId":"Apps/Entity","unlisted":false},{"type":"link","label":"Fields","href":"/Apps4xDocs-Docusaurus/docs/Apps/Fields","docId":"Apps/Fields","unlisted":false},{"type":"link","label":"Form","href":"/Apps4xDocs-Docusaurus/docs/Apps/Form","docId":"Apps/Form","unlisted":false}],"href":"/Apps4xDocs-Docusaurus/docs/category/apps"},{"type":"category","label":"Forms","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Table","href":"/Apps4xDocs-Docusaurus/docs/Forms/Table","docId":"Forms/Table","unlisted":false},{"type":"link","label":"Card","href":"/Apps4xDocs-Docusaurus/docs/Forms/Card","docId":"Forms/Card","unlisted":false},{"type":"link","label":"Columns","href":"/Apps4xDocs-Docusaurus/docs/Forms/Columns/","docId":"Forms/Columns/Columns","unlisted":false},{"type":"link","label":"Condition","href":"/Apps4xDocs-Docusaurus/docs/Forms/Condition/","docId":"Forms/Condition/Condition","unlisted":false},{"type":"link","label":"Configuration","href":"/Apps4xDocs-Docusaurus/docs/Forms/Configuration/","docId":"Forms/Configuration/Configuration","unlisted":false},{"type":"link","label":"DataSource","href":"/Apps4xDocs-Docusaurus/docs/Forms/DataSource/","docId":"Forms/DataSource/DataSource","unlisted":false},{"type":"link","label":"Sorting","href":"/Apps4xDocs-Docusaurus/docs/Forms/Sorting/","docId":"Forms/Sorting/Sorting","unlisted":false}],"href":"/Apps4xDocs-Docusaurus/docs/category/forms"}]},"docs":{"Apps/Apps":{"id":"Apps/Apps","title":"Apps","description":"Apps4x\u2014a platform where you can bring your ideas to life by building custom applications of various types.","sidebar":"tutorialSidebar"},"Apps/Collection":{"id":"Apps/Collection","title":"Collection","description":"Each Application in Apps4x has a Collection, which organizes a group of Entities, their Fields, and associated Forms.","sidebar":"tutorialSidebar"},"Apps/Entity":{"id":"Apps/Entity","title":"Entity","description":"An Entity is a core component within a Collection. It defines the structure of your data by grouping Fields, Forms, Events, Activities, and Actions under one logical unit.","sidebar":"tutorialSidebar"},"Apps/Fields":{"id":"Apps/Fields","title":"Fields","description":"Fields play a crucial role in your application\u2014they define how data is captured, displayed, and stored. Once created, fields can be reused across your forms, entities, and collections as data blueprints.","sidebar":"tutorialSidebar"},"Apps/Form":{"id":"Apps/Form","title":"Form","description":"Forms in Apps4x are versatile components that allow users to view, create, interact with, and manage data within the application. Each form type is tailored to a specific use case or presentation style, providing flexibility in how information is displayed or entered.","sidebar":"tutorialSidebar"},"Forms/Card":{"id":"Forms/Card","title":"Card","description":"The Card form type in Apps4x displays data in a visually rich card layout, offering an alternative to the traditional table view. This layout is ideal for presenting records in a compact, user-friendly, and more engaging format\u2014without writing custom code.","sidebar":"tutorialSidebar"},"Forms/Columns/Columns":{"id":"Forms/Columns/Columns","title":"Columns","description":"Columns in the Table Form define what data is displayed and how it\'s presented in the table view. They allow you to select fields, rearrange their order, show related data, and apply visual enhancements like tags and tag colors for improved readability and user experience.","sidebar":"tutorialSidebar"},"Forms/Condition/Condition":{"id":"Forms/Condition/Condition","title":"Condition","description":"The Condition tab is used to apply logic that controls when and how data is fetched and displayed in a form. It acts as a filter that ensures only records meeting specific criteria are retrieved.","sidebar":"tutorialSidebar"},"Forms/Configuration/Configuration":{"id":"Forms/Configuration/Configuration","title":"Configuration","description":"The Configuration tab provides customization options for layout, visibility, interactivity, and behavior of the table view.","sidebar":"tutorialSidebar"},"Forms/DataSource/DataSource":{"id":"Forms/DataSource/DataSource","title":"DataSource","description":"DataSource defines the origin of data that powers your forms. It allows you to connect forms to different sources such as APIs, queries, collections, or connectors. Configuring a proper DataSource is essential for displaying dynamic and live data on the output page.","sidebar":"tutorialSidebar"},"Forms/Sorting/Sorting":{"id":"Forms/Sorting/Sorting","title":"Sorting","description":"The Sorting feature allows you to control the order in which data appears in a table\u2014either in ascending or descending order\u2014based on a specific field (column).","sidebar":"tutorialSidebar"},"Forms/Table":{"id":"Forms/Table","title":"Table","description":"The Table form type in Apps4x allows you to display and manage data in a structured, grid-like format. It\'s ideal for listing records, enabling quick actions, and applying filters or sorting directly within the UI.","sidebar":"tutorialSidebar"},"Introduction/Introduction":{"id":"Introduction/Introduction","title":"Introduction","description":"Apps4x is a powerful, no-wait, full-stack application development platform that enables anyone\u2014from developers to business users\u2014to build complete applications quickly and efficiently through dynamic configuration.","sidebar":"tutorialSidebar"},"Organization/Company":{"id":"Organization/Company","title":"Company","description":"How to Create company ?","sidebar":"tutorialSidebar"},"Organization/Environment":{"id":"Organization/Environment","title":"Environment","description":"An Environment in Apps4x is a dedicated workspace where you can manage and organize different configurations, applications, and user permissions. It allows you to control who has access to specific resources and how your apps are structured across different environments (e.g., development, staging, production).","sidebar":"tutorialSidebar"},"Organization/Organization":{"id":"Organization/Organization","title":"Organization","description":"An Organization in Apps4x is a shared workspace where you and your team can build, manage, and collaborate on multiple applications. It helps you organize projects, control access, and unlock features based on your selected plan.","sidebar":"tutorialSidebar"}}}}')}}]);